/* eslint-disable react/jsx-pascal-case */
import React, { Component } from 'react';
import Poor_Son from "./Poor_Son";
import Poor_Daughter from "./Poor_Daughter";
class Poor_Parent extends Component{
  constructor(props) {
    super(props);
    this.state={ 
      moneyForSon:60,
      moneyForDaughter:40,
      moneyTotal:100
    }
    this.allocateMoney=this.allocateMoney.bind(this); // bind 這個方法
  }

    allocateMoney(target,amount){
        if(target==="Son") // 這個 target 的參數沒有被設定，但它可以分辯出觸發的元素
            this.setState({moneyForSon:amount}) 
        else
            this.setState({moneyForDaughter:amount});
    }

    render(){
        return( // allocateMoney 的方法，改名為 argue 的方法，傳給兒子。
          <div>
              <Poor_Son 
              money={this.state.moneyForSon} 
              moneySister={this.state.moneyForDaughter}
              argue={this.allocateMoney} /> 
              
              <Poor_Daughter 
              money={this.state.moneyForDaughter} 
              moneyBrother={this.state.moneyForSon}
              argue={this.allocateMoney}/>
          </div>
        );
    }
}
export default Poor_Parent;
