import React, { Component } from 'react';
class Son extends Component{
  constructor(props) {
    super(props);
    this.state={ // 宣告state物件，內包含一個變數percent
      feeling:"bad" 
    }
    this.argueFor70NTD=this.argueFor70NTD.bind(this);
  }

    componentDidMount(){
    }

    argueFor70NTD(){ // 這個方法，取得爸爸給他的 argue 方法。傳入相應的 target 和 amount 參數
        this.props.argue("Son",70);
    } 

    render(){ // 於是爸爸給的 this.props.money 值改變了，爸爸重新render，兒子拿到更多錢。
        return(<div>我是兒子，我拿到{this.props.money}<button onClick={this.argueFor70NTD}>要求提升到70塊</button></div>);
    }
}
export default Son;
