import React, { Component } from 'react';
class Son extends Component{
  constructor(props) {
    super(props);
    this.state={ 
      feeling:"bad",
      expectAmount: 100
    }
    this.argueForMore=this.argueForMore.bind(this); // 記得要註冊方法
    this.setMyFeeling=this.setMyFeeling.bind(this);
  }

    componentDidMount(){
    }

    argueForMore(){ // 這個方法，取得爸爸給他的 argue 方法。傳入相應的 target 和 amount 參數
        this.props.argue("Son",this.props.money+30);
    } 

    setMyFeeling(){
      if(this.props.money<this.state.expectAmount)
          this.setState({feeling:"不能接受"})
      else
          this.setState({feeling:"可以接受"})
    }
    
    componentDidUpdate(prevProps, prevState, snapshot){
      if(prevProps.money!==this.props.money){
          this.setMyFeeling();
      }
    } 

    render(){ // 於是爸爸給的 this.props.money 值改變了，爸爸重新render，兒子拿到更多錢。
        return(<div>我是兒子，我拿到{this.props.money}<button onClick={this.argueForMore}>要求多30</button></div>);
    }
}
export default Son;
