import React,{useState} from "react";

const ComForm=()=>{

    const [account,setAccount]=useState("");
    return (
        <div>
            <p>第一個簡單例子</p>
            <input type="text" onChange={(e)=>{setAccount(e.target.value)}}/>
            <span>  輸入的影子:{account}</span>
            <hr/>
        </div>
    )
}
export default ComForm;
