import React, { Component } from 'react';
class GrandPa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Money: 10
        }
    }
    render() {

        return (
            <Son money={this.state}/>
        );
    }
}
class Son extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Money: props.Money
        }
    }
    render() {
        return (
            <GrandSon money={this.state}/>
        );
    }
}
class GrandSon extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Money: props.Money
        }
    }
    render() {
        return (
            <div>
                I have money : {this.state.Money}
            </div>
        );
    }
}
export {GrandPa, Son, GrandSon} ;
