import React from "react";

// React.createContext 是建立一個 Global State
export const FruitContext = React.createContext({
    appleContext: "abc" //<-- 這個值會被改變，在FruitStore 呼叫並利用它的時候。所以塞什麼都可以。
    //appleContext: 0
})
