import React,{useContext} from 'react';
import {FruitContext} from "./GlobalFruitContext.js";

function Peter() {
    const fruitInfo=useContext(FruitContext);
    return (
    <div className="Peter">
        Peter看到了 [ {fruitInfo.appleContext} ] 個蘋果
    </div>
  );
}

export default Peter;
