import React, { Component } from 'react';
class GrandPa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Money: 10
        }
    }
    render() {

        return (
            <div>
                <div>
                    GrandPa Have : {this.state.Money}
                </div>
                <Son legacy={this.state} />
            </div>
        );
    }
}
class Son extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Money: props.legacy.Money - 1// <--注意這個物件的層級
        }
    }

    handleSendData(name) {
        return this.props[name]; // 這是爸爸自己有的錢。
    }

    handleSendFunc(method, arg_name) {
        return this.props[method](arg_name)-1; //在這個例子中，爸爸給兒子少一塊。
    }

    render() {
        return (
            <div>
                <GrandSon
                    handleSendData={this.handleSendData}
                    handleSendFunc={this.handleSendFunc}
                />
            </div>
        );
    }
}
class GrandSon extends Component {
    // constructor(props) {
    //     super(props);
    //     this.state = {
    //         Money: props.legacy.Money - 1// <--注意這個物件的層級
    //     }
    // }
    render() {
        return (
            <div>
                <div>
                    Son Have : {this.handleSendData("Money")}
                </div>
                <div>
                    GrandSon have : {this.handleSendFunc("handleSendData","money")}
                </div>
            </div>
        );
    }
}
//export {GrandPa, Son, GrandSon} ; // 如果輸出三個，就也要引用三個。
export default GrandPa;
