import React, { Component } from 'react';
import Son from "./Son";
import Daughter from "./Daughter";
class Parent extends Component{
  constructor(props) {
    super(props);
    this.state={ 
      moneyForSon:60,
      moneyForDaughter:40
    }
    this.allocateMoney=this.allocateMoney.bind(this); // bind 這個方法
  }

    allocateMoney(target,amount){
        if(target==="Son") // 這個 target 的參數沒有被設定，但它可以分辯出觸發的元素
            this.setState({moneyForSon:amount}) 
        else
            this.setState({moneyForDaughter:amount});
    }

    render(){
        return(// allocateMoney 的方法，作為argue屬性，傳給兒子。
          <div>
              <Son money={this.state.moneyForSon} argue={this.allocateMoney} /> 
              <Daughter money={this.state.moneyForDaughter} argue={this.allocateMoney}/>
          </div>
        );
    }
}
export default Parent;
