import React, { useState } from 'react';
import Baby2 from './Baby2'

// 和 App 生孩子的方法對比
// 這個是 function component 

const App2=()=>{
    const [dad, setDad] = useState("生父張先生");
    const [born, setBorn] = useState(true);
    
    const changeDad=()=>{
      if(dad==="生父張先生"){
        setDad("改成後爸老王")
      }
      else{
        setDad("生父張先生")
      }
    }

    const GenBaby=()=>{
      if(born===true){
        return <Baby2 dad={dad}/>;
      }
    }

    return(
      <div>
        {GenBaby()}
        <div id="talk"></div>
        <button onClick={changeDad}>換爸爸!</button>
        <button onClick={()=>{setBorn(!born)}}>{(born===true)?"讓他回去肚子裡":"讓他生"}</button>
      </div>
    );
}
export default App2;
