import React, { Component } from 'react';
class GrandPaPa extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Money: 10
        }
    }
    render() {

        return (
            <div>
                <div>
                    GrandPa Have : {this.state.Money}
                </div>
                <Son legacy={this.state}/>
            </div>
        );
    }
}
class Son extends Component {
    constructor(props) {
        super(props);
        this.state = {
            Money: props.legacy.Money - 1// <--注意這個物件的層級
        }
    }

    handleSendData(name) {
        return this.state[name]; // 這是爸爸自己有的錢。
        //return name;
    }

    handleSendFunc(method, arg_name) {
        return this.props[method](arg_name)-1; //在這個例子中，爸爸給兒子少一塊。
    }

    render() {
        return (

            <div>
                 {this.handleSendData("Money")}
                <GrandSon
                    handleSendData={this.handleSendData}
                    handleSendFunc={this.handleSendFunc}
                />
            </div>
        );
    }
}
class GrandSon extends Component {
    constructor(props) {
        super(props);
        this.state = {
            dadMoney: this.props.handleSendData("Money"),
            //myMoney:this.propshandleSendData("handleSendData","Money")
        }

    }


    render() {
        return (
            <div>
                <div>
                    Son Have : {this.state.dadMoney}
                </div>
                {/* <div>
                    GrandSon have : {this.handleSendFunc("handleSendData","Money")}
                </div> */}
            </div>
        );
    }
}

export default GrandPaPa;
