import React, { useState, useEffect } from 'react';

// 和隔壁的Baby(class component)對比
// 這個是 function compoent
const Baby2=(props)=>{ //這個 Baby2 的方法，接收 props 的參數

    /* 把state變成useState */

    const [isGetData,setGetData]=useState(false);   
    const [Mom,setMom]=useState("");               
    const [isRightDad,setRightDad]=useState(true); 
    
    // useState 是什麼？
    // 官網解釋︰const [state, setState] = useState(initialState);
    // 簡化宣告變數，設定初始值及其更改變數的方法。

    /* 把class 的 member function改成「function中的function」 */

    // 單寫這個方法，在孩子出生後，也不會被自動觸發去找媽媽。
    const ajaxSimulator=()=>{
        setTimeout(()=>{
            setGetData(true);
            setMom("May");
        },3000)
    }

    // 單寫這個方法，會無法召喚多次。只有第一次生成才會跑。
    const checkDad=()=>{
        if(props.dad==="Chang")
            setRightDad(true)
        else
            setRightDad(false)
    }

    // 它放的位置是有講究的，不能放在 If 後面
    useEffect(() => {
        ajaxSimulator();
        /* 上面是 componentDidMount */  
    }, []);

    if(isRightDad===false)
        return(
            <div>因為是後爸，所以不找媽媽了。</div>
        ); 
        //是生父，就去找媽媽，但還沒找到
    else if(isGetData===false)
        return(
            <div id="msg">讀取媽媽中</div>
        );
        // 找到媽媽了。
    else
        return(
            <div id="msg">他的媽媽是{Mom}</div>
    );  
    // 這個方法中，唯一個If else，可能是因為涉及return ，所以分開多個if else 就會出錯。

}
export default Baby2;
