import React,{useState} from 'react';
import Amy from './Amy.js';
import Peter from './Peter.js';
import {GlobalFruitContext} from "./GlobalFruitContext.js";

function FruitStore() {
    const [ apple, setApple ] = useState(0);
    return (
        <>
            <div className="FruitStore">目前水果店有 [ {apple} ] 個蘋果</div>
            <GlobalFruitContext.Provider value={{ appleContext:apple }} >
                <Amy/>
                <Peter/>
            </GlobalFruitContext.Provider>
        </>
    );
}

export default FruitStore;
