    const [ apple, setApple ] = useState(1);
    return (
        <>
            <div className="BeforeStore">目前水果店有 [ {apple} ] 個蘋果</div>
            <BeforeAmy apple={apple}/>
        </>
    );
}

export default BeforeStore;
